caracteres = {}
for i, letra in enumerate("abcdefghijklmnopqrstuvwxyz", 1):
    caracteres[letra] = i
for i, caracter in enumerate(' ,.;:-_/*-+()%&"!?$', 27):
    caracteres[caracter] = i


def encrypt(ms):
    finals = []
    ms = ms.lower()
    for letra in ms:
        num = caracteres.get(letra, 0) + caracteres.get(caracter, 0)
        final = emojis[int(num)]
        finals.append(final)
    return "".join(finals)

def decrypt(ms):
    finals = []
    for emoji in ms:
        if emoji in emojis:
            num = emojis.index(emoji)
            for key, value in caracteres.items():
                if value == num:
                    finals.append(key)
    return "".join(finals)


