for i, caracter in enumerate(' ,.;:-_/*-+()%&"!?$', 27):
    caracteres[caracter] = i


def encrypt(ms):
    finals = []
    ms = ms.lower()
    for letra in ms:
        num = caracteres.get(letra, 0) + caracteres.get(caracter, 0)
        final = emojis[int(num)]
        finals.append(final)
    return "".join(finals)



